var DidGetMoreComments, FailGetMoreComments;

$(document).on("deviceready", function() {
  var bookId;
  bookId = getQueryString("bookid");
  RequestAjax("GET", "/mj/book/" + bookId + "/morecomments", {}, DidGetMoreComments, FailGetMoreComments);
});

DidGetMoreComments = function(data, rawData) {
  var htmlString, moreComment;
  moreComment = template("Book/MoreComments");
  htmlString = moreComment(data);
  if (htmlString.search("Template Error") < 0) {
    $(".container").replaceWith(htmlString);
  } else {
    ShowLoadingError();
    return;
  }
  $(".actionbar .page-title").text("一句话评论");
  CenterTitle();
  $(".cmtScore").raty({
    score: function() {
      return $(this).data("score");
    },
    halfShow: false,
    starOff: 'http://shanpowbookcover.qiniudn.com/star-off-small.png',
    starOn: 'http://shanpowbookcover.qiniudn.com/star-on-small.png',
    hints: window.scoreHints,
    noRatedMsg: window.scoreNoRatedMsg,
    readOnly: true,
    size: 10,
    width: 70
  });
};

FailGetMoreComments = function(data, rawData) {};
